let popupContainer=document.getElementById("popupContainer"),openBtn=document.getElementById("popupBtn"),counterInfo=document.getElementById("counter"),resetBtn=document.getElementById("resetBtn"),closeBtn=document.getElementById("closeBtn");function openPopup(){localStorage.setItem("counter",parseInt(localStorage.getItem("counter"))+1),updatePopupContent(),popupContainer.style.display="flex",5<parseInt(localStorage.getItem("counter"))?resetBtn.style.display="block":resetBtn.style.display="none"}function closePopup(){popupContainer.style.display="none"}function resetCounter(){localStorage.setItem("counter","0"),updatePopupContent()}function outsideClick(e){e.target==popupContainer&&(popupContainer.style.display="none")}function updatePopupContent(){counterInfo.innerText=JSON.stringify(localStorage.getItem("counter"))}window.addEventListener("click",outsideClick),openBtn.addEventListener("click",openPopup),resetBtn.addEventListener("click",resetCounter),closeBtn.addEventListener("click",closePopup);